import React from 'react';
import '../styles/Calendar.css';
import leftArrow from '../images/arrow-left.png';
import rightArrow from '../images/arrow-right.png';
import leftDoubleArrow from '../images/arrow-double-left.png';
import rightDoubleArrow from '../images/arrow-double-right.png';
import { dateToDateObj, getMonthYear, getWeekDays, getLastMonthTotalDates, getCurrMonthTotalDates, getCurrMonthStartingDay, getNextMonthStartingDay, goToPrevYear, goToNextYear, goToPrevMonth, goToNextMonth, getDates} from '../utils/calendar';
import SingleDateCalendar from './SingleDateCalendar';
import { setDisplayConditions, filterDisplayEvents } from '../actions/displayArea';
import store from '../store';
import { OVERALL_CALENDAR } from '../utils/constants';

class Calendar extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            markDate: dateToDateObj(new Date()),
            activeDate: dateToDateObj(new Date())
        }
    }
    render() {
        return <div className={this.props.type ? "event-calendar" : "calendar"}>
            <div className={this.props.type ? "event-calendar-interface" : "calendar-interface"}>
                <img onClick={goToPrevYear.bind(this)} className="arrow left-double-arrow" src={leftDoubleArrow} />
                <img onClick={goToNextYear.bind(this)} className="arrow right-double-arrow" src={rightDoubleArrow} />
                <img onClick={goToPrevMonth.bind(this)} className="arrow left-arrow" src={leftArrow} />
                <img onClick={goToNextMonth.bind(this)} className="arrow right-arrow" src={rightArrow} />
                <span className={this.props.type ? "event-calendar-month" : "month"}>{getMonthYear(this.state.markDate)}</span>
            </div>
            <div className={this.props.type ? "event-calendar-dates" : "dates"}>
                {getWeekDays(false)}
                {getDates.call(this, this.state.markDate, this.state.activeDate, this.props.type || OVERALL_CALENDAR)}
            </div>
                </div>;
    }
}

export default Calendar;
